import { getAgreementList, deleteAgreement, auditAgreement, notAuditAgreement, signAgreement } from "./api.js";
import add from "./add.vue";
import edit from "./edit.vue";
import uploadFileInput from "@/views/template/upload-file-input";
export default {
  name: "single-window",
  components: {
    add,
    edit,
    uploadFileInput
  },
  data() {
    return {
      signModal: false,
      signFile: "",
      signLoading: false,
      signObj: {},
      tableHeight: 0,
      selected: ["选择", "序号", "合同标题", "甲方", "乙方", "合同原文件", "签订状态", "合同文件", "签订时间", "发起人", "操作"],
      modal1: false,
      openSearch: true,
      // 显示搜索
      openTip: true,
      // 显示提示
      formData: {},
      currView: "index",
      loading: true,
      // 表单加载状态
      searchForm: {
        // 搜索框初始化对象
        pageNumber: 1,
        // 当前页数
        pageSize: 15,
        // 页面大小
        sort: "createTime",
        // 默认排序字段
        order: "desc",
        // 默认排序方式
        auditStatus: "已审批"
      },
      selectList: [],
      // 多选数据
      selectCount: 0,
      // 多选计数
      selectRow: 0,
      columns: [
      // 表头
      {
        type: "selection",
        width: 60,
        title: "选择",
        align: "center",
        fixed: "left"
      }, {
        title: "序号",
        width: 85,
        align: "center",
        fixed: "left",
        sortType: true,
        render: (h, params) => {
          return h("span", params.index + (this.searchForm.pageNumber - 1) * this.searchForm.pageSize + 1);
        }
      }, {
        title: "合同标题",
        key: "title",
        minWidth: 180,
        tooltip: true,
        sortable: false
      }, {
        title: "甲方",
        key: "firstParty",
        minWidth: 160,
        tooltip: true,
        sortable: false
      }, {
        title: "乙方",
        key: "secondParty",
        minWidth: 160,
        tooltip: true,
        sortable: false
      }, {
        title: "合同原文件",
        key: "file1",
        minWidth: 150,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          if (params.row.file1 == undefined || params.row.file1 == "") {
            return h("div", [h("span", {
              style: {
                color: "#ff9900"
              }
            }, "未上传")]);
          } else {
            return h("img", {
              attrs: {
                src: require("@/assets/pdf.png")
              },
              style: {
                cursor: "zoom-in",
                width: "60px",
                margin: "10px 0",
                "object-fit": "contain"
              },
              on: {
                click: () => {
                  this.showFile(params.row.file1);
                }
              }
            });
          }
        }
      }, {
        title: "审批状态",
        key: "auditStatus",
        minWidth: 150,
        tooltip: true,
        sortable: false
      }, {
        title: "审批人",
        key: "auditUser",
        minWidth: 150,
        tooltip: true,
        sortable: false
      }, {
        title: "审批时间",
        key: "auditTime",
        minWidth: 180,
        tooltip: true,
        sortable: false
      }, {
        title: "签订状态",
        key: "signStatus",
        minWidth: 150,
        tooltip: true,
        sortable: false
      }, {
        title: "合同文件",
        key: "file2",
        minWidth: 150,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          if (params.row.file2 == undefined || params.row.file2 == "") {
            return h("div", [h("span", {
              style: {
                color: "#ff9900"
              }
            }, "未上传")]);
          } else {
            return h("img", {
              attrs: {
                src: require("@/assets/pdf.png")
              },
              style: {
                cursor: "zoom-in",
                width: "60px",
                margin: "10px 0",
                "object-fit": "contain"
              },
              on: {
                click: () => {
                  this.showFile(params.row.file2);
                }
              }
            });
          }
        }
      }, {
        title: "签订时间",
        key: "signTime",
        minWidth: 180,
        tooltip: true,
        sortable: false
      }, {
        title: "发起人ID",
        key: "signId",
        minWidth: 180,
        tooltip: true,
        sortable: false
      }, {
        title: "发起人",
        key: "signUser",
        minWidth: 150,
        tooltip: true,
        sortable: false
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "创建者",
        key: "createBy",
        sortable: true,
        sortType: "desc",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "修改时间",
        key: "updateTime",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "修改者",
        key: "updateBy",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "操作",
        key: "action",
        align: "center",
        width: 300,
        fixed: "right",
        render: (h, params) => {
          var that = this;
          return h("div", [h("Button", {
            props: {
              type: "success",
              size: "small",
              icon: "md-checkmark",
              ghost: true,
              disabled: params.row.signStatus != "未签订" || !(that.$route.meta.permTypes && that.$route.meta.permTypes.includes("enable"))
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.signObj = params.row;
                this.signFile = "";
                this.signModal = true;
              }
            }
          }, "签订"), h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.edit(params.row);
              }
            }
          }, "详情"), h("Button", {
            props: {
              type: "error",
              size: "small",
              icon: "md-trash",
              ghost: true,
              disabled: !(that.$route.meta.permTypes && that.$route.meta.permTypes.includes("delete"))
            },
            on: {
              click: () => {
                this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      // 表单数据
      pageNumber: 1,
      // 当前页数
      pageSize: 10,
      // 页面大小
      total: 0,
      // 表单数据总数
      showFilterPanelFlag: false
    };
  },
  methods: {
    init() {
      this.getDataList();
    },
    signAgreementFx() {
      var that = this;
      that.signLoading = true;
      signAgreement({
        id: that.signObj.id,
        fileUrl: that.signFile
      }).then(res => {
        that.signLoading = false;
        that.signModal = false;
        if (res.success) {
          that.getDataList();
          this.$Message.success("签订成功");
        }
      });
    },
    showFile(e) {
      window.open(e + "?preview=true");
    },
    submited() {
      this.currView = "index";
      this.getDataList();
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    rowClick(row, index) {
      this.selectRow = row;
    },
    rowClassNmae(row, index) {
      if (row.id == this.selectRow.id) {
        return "rowClassNmaeColor";
      }
      return "";
    },
    excelData() {
      this.$refs.table.exportCsv({
        filename: "导出结果"
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      this.searchForm.auditStatus = "已审批";
      this.getDataList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.auditStatus = "已审批";
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      // 重新加载数据
      this.getDataList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getDataList();
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    getDataList() {
      this.loading = true;
      getAgreementList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.data = res.result.records;
          this.total = res.result.total;
        }
      });
    },
    add() {
      this.currView = "add";
    },
    edit(v) {
      // 转换null为""
      for (let attr in v) {
        if (v[attr] == null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      this.formData = data;
      this.currView = "edit";
    },
    auditAgreementFx(v) {
      this.$Modal.confirm({
        title: "确认审核",
        content: "您确认要审核通过 ?",
        loading: true,
        onOk: () => {
          auditAgreement({
            id: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("审核成功");
              this.getDataList();
            }
          });
        }
      });
    },
    notAuditAgreementFx(v) {
      this.$Modal.confirm({
        title: "确认驳回",
        content: "您确认要审核驳回 ?",
        loading: true,
        onOk: () => {
          notAuditAgreement({
            id: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("驳回成功");
              this.getDataList();
            }
          });
        }
      });
    },
    remove(v) {
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除 ?",
        loading: true,
        onOk: () => {
          deleteAgreement({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.getDataList();
            }
          });
        }
      });
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          // 批量删除
          deleteAgreement({
            ids: ids
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.clearSelectAll();
              this.getDataList();
            }
          });
        }
      });
    }
  },
  mounted() {
    this.init();
    this.tableHeight = Number(window.innerHeight - 273);
    this.mycolumns = this.columns;
    let showcolumns = [];
    for (var i = 0; i < this.selected.length; i++) {
      var item = this.selected[i];
      for (var j = 0; j < this.columns.length; j++) {
        if (this.columns[j].title == item) {
          showcolumns.push(this.columns[j]);
        }
      }
    }
    this.columns = showcolumns;
  },
  watch: {
    selected: function (newcolumns) {
      let showcolumns = [];
      for (var i = 0; i < this.mycolumns.length; i++) {
        var item = this.mycolumns[i];
        if (item.title == undefined) showcolumns.push(item);else if (newcolumns.contains(item.title)) showcolumns.push(item);
      }
      this.columns = showcolumns;
    }
  }
};
